const Profile = () => {
  const dispatch = useDispatch();
  const dragons = useSelector((state) => state.dragons.dragons);

  const reservedDragons = dragons.filter((dragon) => dragon.reserved === true);

  useEffect(() => {
    if (!dragons.length) {
      dispatch(fetchData);
    }
  }, []);

  return (
    <div>
      {reservedDragons.length ? (
        <>
          <table className="table-fixed">
            <thead>
              <tr>
                <th className="w-1/2 ...">Title</th>
                <th className="w-1/4 ...">Author</th>
                <th className="w-1/4 ...">Views</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Intro to CSS</td>
                <td>Adam</td>
                <td>858</td>
              </tr>
              <tr className="bg-blue-200">
                <td>
                  A Long and Winding Tour of the History of UI Frameworks and
                  Tools and the Impact on Design
                </td>
                <td>Adam</td>
                <td>112</td>
              </tr>
              <tr>
                <td>Intro to JavaScript</td>
                <td>Chris</td>
                <td>1,280</td>
              </tr>
            </tbody>
          </table>
          <div className="table w-full my-3 px-3">
            <div className="table-row p-2">
              <h2 className="text-2xl py-2 font-bold">My Reserved Dragons</h2>
            </div>
            <ul className="table-cell border w-20 border-gray-500 p-3">
              {reservedDragons.map((dragon) => (
                <li key={dragon.name}>{dragon.name}</li>
              ))}
            </ul>
          </div>
        </>
      ) : (
        <h2 className="p-3 text-2xl font-bold">
          There are currently no dragons reserved.
        </h2>
      )}
    </div>
  );
};
export default Profile;
